sys_ui_action.script:
function x_376096_minify_showPreview() {
	var appSysID = g_form.getUniqueValue();

	getMessage('x_376096_minify_confirm_app_minify_title', function (title) {
		var gm = new GlideModal('x_376096_minify_confirm_minify_app');
		gm.setTitle(title.withValues([g_form.getValue('name')]));
		gm.setPreference('app_sys_id', appSysID);
		gm.setPreference('onSubmit', function () {
			var ga = new GlideAjax('x_376096_minify.MinifyClient');
			ga.addParam('sysparm_name', 'minifyApp');
			ga.addParam('app_sys_id', appSysID);
			ga.getXMLAnswer(function (answer) {
				if (answer) {
					var result = JSON.parse(answer);
					if (result.status == 'success') {
						getMessage('x_376096_minify_successfull_minifying', function (msg) {
							g_form.addInfoMessage(msg);
						});
					} else {
						g_form.addErrorMessage(result.message);
					}
				} else {
					getMessage('x_376096_minify_error_minifying', function (msg) {
						g_form.addErrorMessage(msg);
					});
				}
			});

			gm.destroy();
		});

		gm.render();
	});
}

