sys_ui_page.client_script:
addLoadEvent(function() {
	var gdw = GlideDialogWindow.get();
	var appSysID = gdw.getPreference('app');

	var loadingElement = gel('x_376096_loading');
	var errorElement = gel('x_376096_error');
    var contentElement = gel('x_376096_confirm_content');

	var ga = new GlideAjax('x_376096_minify.MinifyClient');
	ga.addParam('sysparm_name', 'previewMinifyApp');
	ga.addParam('app', appSysID);
	ga.getXMLAnswer(function (answer) {
		loadingElement.style.display = 'none';
		var error;

		if (answer) {
			var result = JSON.parse(answer);
			if (result.status == 'success') {
				contentElement.style.display = '';
				contentElement.innerHTML = 'Do you want to minify ' + result.minify.count + ' records?';
			} else {
				error = result.message;
			}
		} else {
			error = 'Failed to generate preview';
		}

		if (error) {
			errorElement.innerHTML = error;
			errorElement.style.display = '';
		}
	});
});

function execCallback(type) {
	var gdw = GlideDialogWindow.get();
	var callback;
	if (type == 'ok') {
		callback = gdw.getPreference('onConfirmed');
	} else if (type == 'cancel') {
		callback = gdw.getPreference('onCanceled');
	}

	var res;
	if (typeof callback === 'function') {
		res = callback.call();
	}

	if (res && 'then' in res) {
		res.then(function (promiseResult) {
			if (promiseResult || promiseResult === undefined) {
				gdw.destroy();
			}
		});
	} else {
		gdw.destroy();
	}
}
